document.querySelectorAll('.hd-bcve-wrapper').forEach((el,i)=>{var videoId=el.getAttribute('data-id');var template=document.querySelector('#hd-bcve-embed-html-'+videoId);var iframe=template.content.children[0].querySelector('iframe');var iframeSrc=iframe.getAttribute('src')+'&rel=0&showinfo=0&autoplay=1';iframeSrc=iframeSrc.replace('www.youtube.com','www.youtube-nocookie.com');iframe.setAttribute('src',iframeSrc);var caption=template.content.textContent.trim();if(''!==caption){var captionEl=document.createElement('figcaption');captionEl.classList.add('wp-element-caption');captionEl.textContent=caption;el.append(captionEl)}el.addEventListener('click',()=>{var contentOuter=template.content.cloneNode(true);var content=contentOuter.children[0];el.before(content);el.remove();template.remove()})});