document.querySelectorAll('.hd-bcve-wrapper').forEach((el,i)=>{el.addEventListener('click',()=>{var videoId=el.getAttribute('data-id');var template=document.querySelector('#hd-bcve-embed-html-'+videoId);var contentOuter=template.content.cloneNode(true);var content=contentOuter.children[0];var iframe=content.querySelector('iframe');var iframeSrc=iframe.getAttribute('src')+'&autoplay=true&rel=0&showinfo=0';var iframeSrcNew=iframeSrc.replace('www.youtube.com','www.youtube-nocookie.com');iframe.setAttribute('src',iframeSrcNew);el.before(content);el.remove();template.remove()})});