document.querySelectorAll('.hd-bcve-wrapper').forEach((el,i)=>{var template=el.nextElementSibling;var iframe=template.content.children[0].querySelector('iframe');var iframeSrc=iframe.getAttribute('src')+'&rel=0&showinfo=0&autoplay=1';iframeSrc=iframeSrc.replace('www.youtube.com','www.youtube-nocookie.com');iframe.setAttribute('src',iframeSrc);iframe.setAttribute('allow','autoplay');var caption=template.content.children[0].querySelector('figcaption');if(caption){var captionEl=document.createElement('figcaption');captionEl.classList.add('wp-element-caption');captionEl.innerHTML=caption.innerHTML;el.append(captionEl)}var clickEls=[];clickEls.push(el.querySelector('.hd-bcve-thumbnail'));clickEls.push(el.querySelector('.play-button'));clickEls.forEach((clickEls,i)=>{clickEls.addEventListener('click',()=>{var contentOuter=template.content.cloneNode(true);var content=contentOuter.children[0];el.before(content);el.remove();template.remove()})})});